var fjsmth = require('./fjsmth');
var FJsmth = fjsmth.FJsmth();

exports.fjsmthClient = function () {
    if ((module.exports.connection) && (module.exports.connection._native)) {
        return module.exports.connection;
    }
    FJsmth.StartLibWork({ServAddr: "i.1wbee.com", Port: 7001});
    module.exports.connection = FJsmth;
    return module.exports.connection;
};

exports.fjsmthClient();